INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-02-20 08:43:58', '2017-02-20 08:43:58', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world__trashed', '', '', '2017-02-20 10:02:25', '2017-02-20 10:02:25', '', 0, 'http://dev-shellshock.pantheonsite.io/?p=1', 0, 'post', '', 1),
(2, 1, '2017-02-20 08:43:58', '2017-02-20 08:43:58', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://dev-shellshock.pantheonsite.io/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-02-20 08:43:58', '2017-02-20 08:43:58', '', 0, 'http://dev-shellshock.pantheonsite.io/?page_id=2', 0, 'page', '', 0),
(7, 1, '2017-02-20 09:30:01', '2017-02-20 09:30:01', 'Welcome to your site! This is your homepage, which is what most visitors will see when they come to your site for the first time.', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2017-02-20 09:30:01', '2017-02-20 09:30:01', '', 0, 'http://dev-shellshock.pantheonsite.io/?page_id=7', 0, 'page', '', 0),
(8, 1, '2017-02-20 09:30:01', '2017-02-20 09:30:01', 'You might be an artist who would like to introduce yourself and your work here or maybe you&rsquo;re a business with a mission to describe.', 'About', '', 'publish', 'closed', 'closed', '', 'about', '', '', '2017-02-20 09:30:01', '2017-02-20 09:30:01', '', 0, 'http://dev-shellshock.pantheonsite.io/?page_id=8', 0, 'page', '', 0),
